#!/bin/bash
echo " \"datetime\": $(grep ro\.build\.date\.utc $OUT/system/build.prop | cut -d= -f2), \"filename\": \"$(basename $(ls $OUT/OneOS*.zip))\", \"url\": \"https://sourceforge.net/projects/caf-test/files/$(grep ro\.one\.device $OUT/system/build.prop | cut -d= -f2)/ten/$(grep ro\.oneos\.releasetype $OUT/system/build.prop | cut -d= -f2)/$(basename $(ls $OUT/OneOS*.zip))\", \"id\": \"$((md5sum $OUT/OneOS*.zip) | cut -d ' ' -f1)\", \"romtype\": $(grep ro\.oneos\.releasetype $OUT/system/build.prop | cut -d= -f2), \"size\": $(stat -c%s $OUT/OneOS*.zip), \"version\": $(grep ro\.oneos\.base\.version $OUT/system/build.prop | cut -d= -f2) " | tee $OUT/update
